var mootools = require('mootools').apply(GLOBAL);

var config =
{
	port : 8080,
	server : 'localhost:8080',
	baseUrl : 'http://localhost:8080/',
	socketServer : 'http://localhost',
	basePath : './',
	log : true,
};

var env = process.env.node_env;
var dir = process.env.node_config?process.env.node_config:'config/';

envConfig = {};

if (env) envConfig = require(dir + 'config.' + env);

exports.CONFIG = Object.merge(config, envConfig);



