var path = require('path');
var CONFIG = require('config').CONFIG;
var url = require('url');
var fs = require('fs');
var Q = require('q');
var paths = require('routePaths');

var processed = {};
var mangler = new CONFIG.mangler();

var CssMangler = new Class({
	initialize : function(req, res, next)
	{
		this.req = req;
		this.res = res;
		this.next = next;
		this.mangler = new CONFIG.mangler();
	},

	deliverCSS : function(content)
	{
		this.res.statusCode = 200;
		this.res.setHeader('Content-Type', 'text/css');

		this.res.end(content);
	},

	getFile: function(url)
	{
		var deferred = Q.defer();
		var filename = paths.urlPathToFilePath(url);

		fs.readFile(filename, function (err, data)
		{
			if (err) deferred.resolve(null);
			else deferred.resolve(data.toString());
		});

		return deferred.promise;
	},

	getUrls : function(content)
	{
		var urls = [];
		var regex = /url\(['"]?(.*?)['"]?\)/gim;
		var url = regex.exec(content);
		while (url)
		{
			urls.push(url[1]);
			var url = regex.exec(content);
		}

		return urls;
	},

	mangleUrls : function(content, urls)
	{
		return this.mangler.mangleUrls(urls)
			.then(function()
			{
				urls.each(function(url)
				{
					var mangled = mangler.mangle(url);
					content = content.split(url).join(mangled);
				}, this);

				return Q(content);
			}. bind(this))
	},

	process : function()
	{
		req = this.req;
		res = this.res;
		next = this.next;

		if (processed[req.url] && CONFIG.builderCache) return deliverCSS(processed[req.url]);

		this.getFile(req.url.split('?')[0])
			.then(function(content)
			{
				if (content == null) next();
				var urls = this.getUrls(content);
				return this.mangleUrls(content, urls)
					.then(function(content)
					{
						processed[req.url] = content;
						this.deliverCSS(content);
						return Q(true);
					}.bind(this));
			}.bind(this))
			.done();
	}
});

function processCSS (req, res, next)
{
	var cssMangler = new CssMangler(req, res, next);

	cssMangler.process();
}

module.exports = function()
{
	return function(req, res, next)
	{
		req.url = mangler.demangle(req.url);
		var url = req.url.split('?')[0];
		var ext = path.extname(url);
		if (url.indexOf('/assets/') != -1 && ext == '.css')	processCSS(req, res, next)
		else next();
	}
}
